var order = global.get("order")
if (Array.isArray(order) && order.length != msg.payload.length){
    msg.payload.map((x,id) => {
        x["id"] = id+1;
        x["time"] = new Date(x["time"]).toLocaleString()
        return x;
    });
    global.set("ready", msg.payload.filter((item) =>{
        return item.id != global.get("prd") && item.id != global.get("created")
    }).map((x) => x.id))

    global.set("order", msg.payload);
    return msg;
}

if (msg.payload <= global.get("order").length){
    msg.select = global.get("order").find((x) => {
        return x.id == msg.payload;
    }).order_id;
}
return msg;

if (msg.select != undefined){
    msg.topic = "SELECT oc_order_product.name as product, oc_order_product.quantity, oc_order_option.value as procs, oc_order_product.model as material FROM `oc_order_product` LEFT JOIN oc_order_option ON oc_order_option.order_product_id=oc_order_product.order_product_id WHERE oc_order_product.order_id="+msg.select+";";
    return msg;
}

var ready = global.get("ready")
var order = global.get("order")

msg.payload = ready.map((x) => {
    return order.find((y) => x==y.id)
})
return msg;

var created = global.get("created")
var order = global.get("order")

msg.payload = created.map((x) => {
    return order.find((y) => x==y.id)
})
return msg;

var item = global.get("order").find((x) => x.id == global.get("prd"))
if (item !=undefined){
    msg.select = item.order_id
    msg.buyer = item.buyer
    msg.time = item.time
    msg.price = item.price
    msg.id = item.id
    msg.payload = item
}else{
    msg.select = undefined
    msg.buyer = ""
    msg.time = ""
    msg.price = ""
    msg.id = ""
    msg.payload = ""
}

return msg;

var ready = global.get("ready");
var index = ready.indexOf(flow.get("emerge"))
if (index != -1){
    var x = ready.splice(index,1)[0];
    ready.splice(0,0,x);
    global.set("ready", ready);
    msg.payload = ready;
    return msg;
}

msg.ready = global.get("ready").length
msg.created = global.get("created").length
return msg;

var ready = global.get("ready")
if (ready.length > 0 && global.get("prd") == undefined && msg.payload){
    var item = ready.shift()
    global.set("prd", item);
    global.set("ready", ready);
    return msg;    
}

var prd = global.get("prd")
if (prd != undefined){
    var created = global.get("created")
    global.set("prd", undefined)
    created.push(prd)
    global.set("created", created)
    return msg;
}
