const pop = (val, arr) => {
    var ind = arr.indexOf(val)
    if (ind != -1){
        arr.splice(ind,1)
    }
    return arr
}

var order = global.get("order")
if (Array.isArray(order) && order.length != msg.payload.length){
    msg.payload.map((x,id) => {
        x["id"] = id+1;
        x["time"] = new Date(x["time"]).toLocaleString()
        return x;
    });
    //diff
    //substract or apppend
    var ready = global.get("ready")
    //substract
    if (msg.payload.length < order.length){
        // Leave a space hehe
    }else{
        //append
        var rm_cr_prd = msg.payload.filter((item) =>{
            return item.order_id != global.get("prd") && !global.get("created").includes(item.order_id)
        }).map(x => x.order_id)
        ready.forEach(function(element) {
            rm_cr_prd = pop(element,rm_cr_prd)
        });
        ready = ready.concat(rm_cr_prd)
    }
    global.set("ready",ready)
    global.set("order", msg.payload);
    return msg;
}

if (msg.payload <= global.get("order").length){
    msg.select = global.get("order").find((x) => {
        return x.id == msg.payload;
    }).order_id;
}
return msg;

if (msg.select != undefined){
    let prod = global.get("products")
    if (Array.isArray(prod)){
        msg.payload = prod.filter(x=>x.order_id==msg.select)
    }else{
        msg.payload = undefined
    }

}else{
    msg.payload = [{"product":"","quantity":"", "procs":"", "material":""}]
}
return msg;

var ready = global.get("ready")
var order = global.get("order")

msg.payload = ready.map((x) => {
    return order.find((y) => x==y.order_id)
})
return msg;

var created = global.get("created")
var order = global.get("order")

msg.payload = created.map((x) => {
    return order.find((y) => x==y.order_id)
})
return msg;

var item = global.get("order").find((x) => x.order_id == global.get("prd"))
if (item !=undefined){
    msg.select = item.order_id
    msg.buyer = item.buyer
    msg.time = item.time
    msg.price = item.price
    msg.id = item.id
    msg.payload = item
}else{
    msg.select = undefined
    msg.buyer = ""
    msg.time = ""
    msg.price = ""
    msg.id = ""
    msg.payload = ""
}

return msg;

var ready = global.get("ready");
var order = global.get("order")
var index = ready.indexOf(order.find((x) => {
    return flow.get("emerge") == x.id
}).order_id)
if (index != -1){
    var x = ready.splice(index,1)[0];
    ready.splice(0,0,x);
    global.set("ready", ready);
    msg.payload = ready;
    return msg;
}

let products = Array.from(global.get("products"))
let ready = Array.from(global.get("ready"))
let created = Array.from(global.get("created"))

msg.ready = products.filter(x=>ready.includes(x.order_id)).length
msg.created = products.filter(x=>created.includes(x.order_id)).length
return msg;

if (global.get("prd") != undefined){
    global.set("producing", msg.payload)
}

return msg;

let products = Array.from(global.get("products"))
let prdprd = global.get("products_produced")

msg.created = prdprd.length
msg.ready = products.length - msg.created
return msg;

var ready = global.get("ready")
if (ready.length > 0 && global.get("prd") == undefined && msg.payload){
    var item = ready.shift()
    global.set("prd", item);
    global.set("ready", ready);
    return msg;    
}
if (Array.isArray(global.get("producing"))&& global.get("producing").length != 0){
    return msg;

}

var prd = global.get("prd")
if (prd != undefined){
    var created = global.get("created")
    global.set("prd", undefined)
    created.push(prd)
    global.set("created", created)
    return msg;
}

if (msg.payload[0] == 0 && Array.isArray(global.get("producing") && Array.from(global.get("producing")).length == 0)){
    return msg;
}

var producing = global.get("producing")
var condi = msg.payload[4];
var prdprd = global.get("products_produced")

if (Array.isArray(producing)){
    if (condi == 1){
        var f = producing.findIndex(x => x.product == "Standard product")
        if (f == -1){
            msg.payload =  5;
        }else{
            let x = producing.splice(f, 1)[0];
            prdprd.push(x)
            msg.payload = 0;
        }

    }

    if (condi == 2){
        var f = producing.findIndex(x => x.product == "Customized product")
        if (f == -1){
            msg.payload = 5;
        }else{
            let procs = producing[f].procs
            let x = producing.splice(f, 1);
            prdprd.push(x)
            msg.payload = procs;
        }
    }

    if(condi != 0){
        global.set("products_produced", prdprd)
        global.set("producing", producing)
        return msg;
    }

}
